public class GovernmentOffice {
    private static volatile GovernmentOffice instance;
    private String officeName;
    private List<String> documents;

    private GovernmentOffice() {
        this.officeName = "Central Government Office";
        this.documents = new ArrayList<>();
        System.out.println("Sharma Ji: Government office is now established!");
    }

    public static GovernmentOffice getInstance() {
        if (instance == null) {
            synchronized (GovernmentOffice.class) {
                if (instance == null) {
                    instance = new GovernmentOffice();
                }
            }
        }
        return instance;
    }

    public void processDocument(String document) {
        documents.add(document);
        System.out.println("Sharma Ji processed: " + document);
    }

    public void showStatus() {
        System.out.println("Office: " + officeName);
        System.out.println("Documents processed: " + documents.size());
    }
}

class GovernmentOffice {
    constructor() {
        if (GovernmentOffice.instance) {
            return GovernmentOffice.instance;
        }

        this.officeName = 'Central Government Office';
        this.documents = [];
        console.log('Sharma Ji: Government office is now established!');

        GovernmentOffice.instance = this;
        return this;
    }

    processDocument(document) {
        this.documents.push(document);
        console.log(`Sharma Ji processed: ${document}`);
    }

    showStatus() {
        console.log(`Office: ${this.officeName}`);
        console.log(`Documents processed: ${this.documents.length}`);
    }
}

// Usage
const office1 = new GovernmentOffice();
const office2 = new GovernmentOffice();

console.log(office1 === office2); // true - same instance!

// Product interface
public interface Toy {
    void play();
}

// Concrete Products
public class PuzzleToy implements Toy {
    @Override
    public void play() {
        System.out.println("Solving the puzzle challenge!");
    }
}

public class MusicalToy implements Toy {
    @Override
    public void play() {
        System.out.println("Playing a beautiful melody!");
    }
}

public class ActionFigureToy implements Toy {
    @Override
    public void play() {
        System.out.println("Saving the world with action moves!");
    }
}

// Creator - Abstract Factory
public abstract class ToyMaker {
    // Factory Method
    public abstract Toy createToy();

    // Template method that uses the factory method
    public Toy deliverToy() {
        Toy toy = createToy();
        System.out.println("Wrapping the toy in colorful paper...");
        return toy;
    }
}

// Concrete Creators
public class PuzzleToyMaker extends ToyMaker {
    @Override
    public Toy createToy() {
        return new PuzzleToy();
    }
}

public class MusicalToyMaker extends ToyMaker {
    @Override
    public Toy createToy() {
        return new MusicalToy();
    }
}

public class ActionFigureToyMaker extends ToyMaker {
    @Override
    public Toy createToy() {
        return new ActionFigureToy();
    }
}

// Client code
public class ToyWorkshop {
    public static void main(String[] args) {
        // Assess the child's personality
        String childPersonality = "creative";

        // Select the appropriate toy maker
        ToyMaker toyMaker;
        switch (childPersonality) {
            case "logical":
                toyMaker = new PuzzleToyMaker();
                break;
            case "musical":
                toyMaker = new MusicalToyMaker();
                break;
            case "active":
                toyMaker = new ActionFigureToyMaker();
                break;
            default:
                toyMaker = new PuzzleToyMaker();
        }

        // Create and deliver the toy
        Toy toy = toyMaker.deliverToy();
        toy.play();
    }
}

from abc import ABC, abstractmethod

# Product interface
class Toy(ABC):
    @abstractmethod
    def play(self):
        pass

# Concrete Products
class PuzzleToy(Toy):
    def play(self):
        print("Solving the puzzle challenge!")

class MusicalToy(Toy):
    def play(self):
        print("Playing a beautiful melody!")

class ActionFigureToy(Toy):
    def play(self):
        print("Saving the world with action moves!")

# Creator - Abstract Factory
class ToyMaker(ABC):
    # Factory Method
    @abstractmethod
    def create_toy(self):
        pass

    # Template method that uses the factory method
    def deliver_toy(self):
        toy = self.create_toy()
        print("Wrapping the toy in colorful paper...")
        return toy

# Concrete Creators
class PuzzleToyMaker(ToyMaker):
    def create_toy(self):
        return PuzzleToy()

class MusicalToyMaker(ToyMaker):
    def create_toy(self):
        return MusicalToy()

class ActionFigureToyMaker(ToyMaker):
    def create_toy(self):
        return ActionFigureToy()

# Client code
def toy_workshop():
    # Assess the child's personality
    child_personality = "creative"

    # Select the appropriate toy maker
    if child_personality == "logical":
        toy_maker = PuzzleToyMaker()
    elif child_personality == "musical":
        toy_maker = MusicalToyMaker()
    elif child_personality == "active":
        toy_maker = ActionFigureToyMaker()
    else:
        toy_maker = PuzzleToyMaker()

    # Create and deliver the toy
    toy = toy_maker.deliver_toy()
    toy.play()

if __name__ == "__main__":
    toy_workshop()

public class IndianWedding {
    private final String bride;
    private final String groom;
    private final LocalDate weddingDate;
    private String venue;
    private String decoration;
    private String catering;
    private boolean mehendi;
    private boolean sangeet;
    private int guestCount;

    private IndianWedding(WeddingBuilder builder) {
        this.bride = builder.bride;
        this.groom = builder.groom;
        this.weddingDate = builder.weddingDate;
        this.venue = builder.venue;
        this.decoration = builder.decoration;
        this.catering = builder.catering;
        this.mehendi = builder.mehendi;
        this.sangeet = builder.sangeet;
        this.guestCount = builder.guestCount;
    }

    public static class WeddingBuilder {
        private final String bride;
        private final String groom;
        private final LocalDate weddingDate;
        private String venue = "Traditional Hall";
        private String decoration = "Standard";
        private String catering = "Gujarati Menu";
        private boolean mehendi = false;
        private boolean sangeet = false;
        private int guestCount = 100;

        public WeddingBuilder(String bride, String groom, LocalDate date) {
            this.bride = bride;
            this.groom = groom;
            this.weddingDate = date;
        }

