            this.mehendi = mehendi;
            return this;
        }

        public IndianWedding build() {
            return new IndianWedding(this);
        }
    }
}

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

// News Type Enum
enum NewsType {
    EMERGENCY,
    WEATHER,
    AGRICULTURE,
    SOCIAL
}

// Observer Interface
interface Villager {
    void receiveNews(NewsType type, String news);
}

// Concrete Observer
class VillageHouse implements Villager {
    private String familyName;

    public VillageHouse(String familyName) {
        this.familyName = familyName;
    }

    @Override
    public void receiveNews(NewsType type, String news) {
        System.out.println(familyName + " received " + type + " news: " + news);
    }
}

// Subject
class VillageCrier {
    private Map<NewsType, List<Villager>> subscribers = new HashMap<>();
    private String name;

    public VillageCrier(String name) {
        this.name = name;
        // Initialize lists for each news type
        for (NewsType type : NewsType.values()) {
            subscribers.put(type, new ArrayList<>());
        }
    }

    // Subscribe to a news type
    public void subscribe(NewsType type, Villager villager) {
        List<Villager> villagers = subscribers.get(type);
        if (!villagers.contains(villager)) {
            villagers.add(villager);
            System.out.println(name + ": New subscriber for " + type + " news!");
        }
    }

    // Unsubscribe from a news type
    public void unsubscribe(NewsType type, Villager villager) {
        List<Villager> villagers = subscribers.get(type);
        if (villagers.remove(villager)) {
            System.out.println(name + ": Subscriber removed from " + type + " news.");
        }
    }

    // Notify all subscribers of a specific news type
    public void announceNews(NewsType type, String news) {
        System.out.println(name + " announces " + type + " news: " + news);

        List<Villager> villagers = subscribers.get(type);
        for (Villager villager : villagers) {
            villager.receiveNews(type, news);
        }
    }
}

// Client code
public class VillageNewsSystem {
    public static void main(String[] args) {
        // Create the village crier
        VillageCrier panditRamesh = new VillageCrier("Pandit Ramesh");

        // Create some villagers
        Villager sharmaHouse = new VillageHouse("Sharma Family");
        Villager patelHouse = new VillageHouse("Patel Family");
        Villager singhHouse = new VillageHouse("Singh Family");

        // Subscribe villagers to different news types
        panditRamesh.subscribe(NewsType.EMERGENCY, sharmaHouse);
        panditRamesh.subscribe(NewsType.EMERGENCY, patelHouse);
        panditRamesh.subscribe(NewsType.EMERGENCY, singhHouse);

        panditRamesh.subscribe(NewsType.WEATHER, sharmaHouse);
        panditRamesh.subscribe(NewsType.AGRICULTURE, patelHouse);
        panditRamesh.subscribe(NewsType.SOCIAL, singhHouse);

        // Announce some news
        panditRamesh.announceNews(NewsType.EMERGENCY, "Heavy rainfall expected tonight. Secure your homes!");
        panditRamesh.announceNews(NewsType.AGRICULTURE, "The rice harvest will begin next week.");

        // Unsubscribe and then announce again
        panditRamesh.unsubscribe(NewsType.EMERGENCY, patelHouse);
        panditRamesh.announceNews(NewsType.EMERGENCY, "The flood warning has been lifted.");
    }
}
