// Topic Story Integration for OOP Java Course
const OOPJavaTopics = {
    'oop-java-topic-1': {
        title: "Maya's Onboarding Challenge: Understanding Objects and Classes",
        sections: [
            {
                id: 'section-1',
                title: 'The Codebase Tour',
                type: 'story',
                content: 'story-content-loaded-from-json'
            },
            {
                id: 'section-2', 
                title: 'Building Your First Class',
                type: 'story',
                content: 'story-content-loaded-from-json'
            },
            {
                id: 'section-3',
                title: 'The Constructor Mystery',
                type: 'story',
                content: 'story-content-loaded-from-json'
            },
            {
                id: 'section-4',
                title: 'Objects in Action',
                type: 'story',
                content: 'story-content-loaded-from-json'
            },
            {
                id: 'section-5',
                title: 'Code Review and Best Practices',
                type: 'story',
                content: 'story-content-loaded-from-json'
            }
        ]
    }
};

// Function to load topic content
function loadOOPJavaTopic(topicId) {
    return fetch(`data/courses/oop-java/${topicId}.json`)
        .then(response => response.json())
        .then(topicData => {
            return {
                ...topicData,
                sections: topicData.sections.map(section => ({
                    ...section,
                    content: section.content
                }))
            };
        });
}

// Export for use in topic-story.html
if (typeof module !== 'undefined' && module.exports) {
    module.exports = { OOPJavaTopics, loadOOPJavaTopic };
}
